import React from 'react';
import { motion } from 'framer-motion'; // for animations, if installed
import { Link } from 'react-router-dom';
import heroImage from '../assets/heroDark.png'; // place any dark-themed hero graphic in assets

export default function LandingPage() {
  return (
    <div className="container-fluid py-5">
      {/* Hero Section */}
      <div className="row align-items-center">
        <div className="col-md-6">
          <motion.h1
            className="mb-4"
            style={{ fontSize: '3rem', color: 'var(--color-accent)' }}
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            Revolutionize Your Trades
          </motion.h1>
          <motion.p
            className="lead mb-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5, duration: 0.8 }}
          >
            A dark, modern platform to track, analyze, and enhance your trading performance.
          </motion.p>
          <Link to="/register" className="btn btn-accent btn-lg">
            Get Started
          </Link>
        </div>
        <div className="col-md-6 text-center">
          <motion.img
            src={heroImage}
            alt="Hero"
            className="img-fluid"
            style={{ maxHeight: '400px' }}
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8 }}
          />
        </div>
      </div>
    </div>
  );
}
